<?php
require_once('interface.auth.php');
require_once('user.php');

class DummyAuth implements iAuth {
	private static $instance;
	
	public static function getInstance(){
		if(isset(self::$instance)) return self::$instance;
		else return false;
	}
	
	public function __construct(){
		self::$instance = $this;
	}

	public function is_connected(){
		return true;
	}
	
	public function logout_user(){
		unset($_SESSION['Dummy']['pass']);
		unset($_SESSION['Dummy']['cache']);
	}

	public function get_users(){
		$users = json_decode(file_get_contents('lib/users.dummy.json'));
		foreach($users as $k=>$u)
			$users[$k] = User::FromLDAP($u);
		$img = "";
		$users[0]->jpegphoto = base64_decode($img);
		
		return $users;
	}

	public function ask_auth(){
		
	}
	public function auth_user($uid, $pass){
		$users = $this->get_users();
		if($uid == 'dummy' && $pass == 'test'){
			$_SESSION['Dummy']['pass'] = $pass;
			$_SESSION['Dummy']['uid'] = $uid;
			$this->user = $users[0];
		} else return false;
	}
	public function is_authenticated(){
		if(!isset($this->user) && $u = $this->get_users()){
			$this->user = $u[0];
		}
		return isset($_SESSION['Dummy']['pass']) ? $this->user : false;
	}
}
?>